    @Test
    void sampleInput_solve_3068() {
        String input = ">>><<><>><<<>><>>><<<>>><<<><<<>><>><<>>";
        RockGame cut = new RockGame();
        int result = cut.solve(input, 2022);
        assertThat(result).isEqualTo(3068);
    }

    @Test
    void sampleInput_solve_17() {
        String input = ">>><<><>><<<>><>>><<<>>><<<><<<>><>><<>>";
        RockGame cut = new RockGame();
        int result = cut.solve(input, 10);
        assertThat(result).isEqualTo(17);
    }

