package day6;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.assertj.core.api.Assertions.assertThat;

class SignalDetectorTest {

    @Test
    public void inputPuzzle_firstMarker_puzzleResult() {
        SignalDetector cut = new SignalDetector();
        String input = "";
        int result = cut.getFirstMarker(input);
        assertThat(result).isEqualTo(1300);
    }

    @ParameterizedTest
    @MethodSource("provideFirstMarkerInput")
    void input_getFirstMarker_firstMarker(
            String input, int expected) {
        SignalDetector cut = new SignalDetector();
        int result = cut.getFirstMarker(input);
        assertThat(result).isEqualTo(expected);
    }

    @ParameterizedTest
    @MethodSource("provideFirstMessageInput")
    void input_getMessage_firstMarker(
            String input, int expected) {
        SignalDetector cut = new SignalDetector();
        int result = cut.getMessageMarker(input);
        assertThat(result).isEqualTo(expected);
    }

    @Test
    public void inputPuzzle_firstMessage_puzzleResult() {
        SignalDetector cut = new SignalDetector();
        String input = "";
        int result = cut.getMessageMarker(input);
        assertThat(result).isEqualTo(3986);
    }

    private static Stream<Arguments> provideFirstMarkerInput() {
        return Stream.of(
                Arguments.of("bvwbjplbgvbhsrlpgdmjqwftvncz", 5),
                Arguments.of("nppdvjthqldpwncqszvftbrmjlhg", 6),
                Arguments.of("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 10),
                Arguments.of("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 11),
                Arguments.of("aaaaaa", -1)
        );
    }

    private static Stream<Arguments> provideFirstMessageInput() {
        return Stream.of(
                Arguments.of("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 19),
                Arguments.of("bvwbjplbgvbhsrlpgdmjqwftvncz", 23),
                Arguments.of("nppdvjthqldpwncqszvftbrmjlhg", 23),
                Arguments.of("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 29),
                Arguments.of("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 26),
                Arguments.of("aa", -1)
        );
    }
}